sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

  if (isLoading || newValue === '') {
      return;
   }

	 //Type appropriate comment here, and begin script below
   var glideAjax = new GlideAjax('GetEmailAddress');
	glideAjax.addParam('sysparm_name','getEmail');
	glideAjax.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	glideAjax.getXML(fetchData);

	function fetchData(response){
		var email = response.responseXML.documentElement.getAttribute('answer');
		console.log('Client Scripting OnChange' +email);
		alert(email);
		g_form.setValue('u_requester_for_email', email);
	}

   //Type appropriate comment here, and begin script below

}
