sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	// Add your code here
	gs.info("TRANSFORM - Within the Transform Map Script");

	var today = new GlideDateTime("2019-02-15 08:00:00");
	if(source.u_when_needed == ''){
		target.u_when_needed = today.getDate();
		gs.info("TRANSFORM - When Needed is "+target.u_when_needed);
	}
	if(source.u_request_for == ''){
		target.u_requested_for = gs.getUserName();
		gs.info('TRANSFORM - User name is '+target.u_requested_for);
	}

	if(source.u_what_needed == ''){
		target.u_what_needed = 'Legal1';
	}

})(source, target, map, log, action==="update");
